resources.string:
val smallScale = 0.3f
 val fullScale = 1

 Orchestra.setup {
 	on(squareLeft, squareCenter, squareRight)
 		 .alpha(0)
 		 .scale(smallScale)
 }
 Orchestra.launch {
 	val duration = 600L
 	val blink = animations()
 		 .parallel {
 			 fadeIn {
 				 this.duration = duration
 			 }
 			 scale(fullScale) {
 				 this.duration = duration
 			 }
 		 }
 		 .parallel {
 			 fadeOut {
 				 this.duration = duration
 			 }
 			 scale(smallScale) {
 				 this.duration = duration
 			 }
 		 }
 		 .delay(600)

 	parallel {
 	 on(squareLeft).repeat(3, blink)
 	 on(squareCenter).delay(400).repeat(3, blink)
 	 on(squareRight).delay(800).repeat(3, blink)
 	}
 }

val slidingViews = arrayOf(titleText, loginText, loginEditText, passwordText, passwordEditText, loginButton)

 Orchestra.setup {
 	 on(form, *slidingViews).invisible()
 }

 Orchestra.launch {
 	 delay(500)

 	 parallel {
 		 val slideDuration = 600L
 		 val halfSlideDuration = slideDuration / 2

 		 on(form).parallel {
 			 slide(Direction.Up) {
 				 duration = slideDuration
 			 }
 			 fadeIn {
 				 duration = halfSlideDuration
 			 }
 		 }

 		 on(*slidingViews)
 				 .delay(halfSlideDuration)
 				 .slide(Direction.Up) {
 					 duration = slideDuration
 				 }
 	 }
 }
